													field="document_no"
													value={issuanceDocumentforEdit.document_no}
													onChange={hasEditIssuanceDocument}
													required={false}
													maxLength={6}
													label="Document Number"
													placeholder="Document Number"
													customError={""}
													disabled={true}
												/>{" "}
											</div>
										</div>
										<div className="row">
											<div
												className="mb-3 col-md-8"
												style={{ textAlign: "left" }}
											>
												<TextInput
													id="input_request_no"
													field="document_name"
													value={issuanceDocumentforEdit.document_name}
													onChange={hasEditIssuanceDocument}
													required={false}
													maxLength={60}
													label="Document Name"
													placeholder="Document Name"
													customError={""}
												/>{" "}
											</div>
										</div>
										<div className="row">
											<div
												className="mb-3 col-md-8"
												style={{ textAlign: "left" }}
											>
												<NumberInput
													id="input_request_no"
													field="no_of_copy"
													value={issuanceDocumentforEdit.no_of_copy}
													onChange={hasEditIssuanceDocument}
													label="No of Copy"
													customError={""}
												/>{" "}
											</div>
										</div>
										<div className="row">
											<div
												className="mb-3 col-md-8"
												style={{ textAlign: "left" }}
											>
												<NumberInput
													id="input_request_no"
													field="no_of_page"
													value={issuanceDocumentforEdit.no_of_page}
													onChange={hasEditIssuanceDocument}
													label="No of page"
													customError={""}
												/>{" "}
											</div>
										</div>

										<div className="row">
											<div
												className="mb-3 col-md-8"
												style={{ textAlign: "left" }}
											>
												<div>
													<TextAreaInput
														id="input_request_no"
														field="reason_for_request"
														value={issuanceDocumentforEdit.reason_for_request}
														onChange={hasEditIssuanceDocument}
														required={false}
														maxLength={100}
														label="Reason for Request"
														placeholder="Reason for Request"
														customError={""}
													/>{" "}
												</div>
											</div>
										</div>
									</div>
								</div>
								<div className="modal-footer">
									<button
										type="button"
										className="btn btn-secondary"
										data-bs-dismiss="modal"
										onClick={() => closeDocUpdate()}
									>
										Cancel
									</button>
									<button
										className="btn btn-primary"
										onClick={() => updateIssuanceDocument()}
									>
										Update Document
									</button>
								</div>
							</div>
						</div>
					</div>
				</Popup>
			</div>
		</Fragment>
	);
};

export default ProductForm;
